symbolsHeader = symbolMapping(bitsHeader, 2, "psk");
sHeader       = pulseShaping(symbolsHeader, osf);
xHeader       = iqModulation(sHeader, Fc);

%% Pilot generation (for channel estimation and equalization)
bitsPilot    = bits(1 : nPilotOFDMsymbols * (nFFT - 1) * log2(M));
symbolsPilot = symbolMapping(bitsPilot, M, modScheme);
symbolsPilot = reshape(symbolsPilot, nFFT - 1, nPilotOFDMsymbols);

%% Data generation (to calculate BER, SER, EVM)
bitsData    = bits(1 : nDataOFDMsymbols * (nFFT - 1) * log2(M));
symbolsData = symbolMapping(bitsData, M, modScheme);

%% Noise Filtering
yFiltered = bandpass(yDSO, (Fc + [-1, 1] * 1.75 * Rs / Fs / 2) * 2);

%% Frame Synchronization
nHeaderSamples = numel(xHeader);
nPilotSamples  = nPilotOFDMsymbols * (nFFT + nCP) * osf;
nDataSamples   = nDataOFDMsymbols * (nFFT + nCP) *osf;
nFrameSamples  = nHeaderSamples + nPilotSamples + nDataSamples;

yFrame = frameSynchronization(yFiltered, xHeader, nFrameSamples); 

%% IQ demodulation
rFrame = iqDemodulation(yFrame, Fc, Rs / Fs);
rPilot = rFrame(nHeaderSamples + (1 : nPilotSamples));
rData  = rFrame(nHeaderSamples + nPilotSamples + (1 : nDataSamples));

%% OFDM demodulation
symbolsPilotReceived = ofdmDemodulation(rPilot, nFFT, nPilotOFDMsymbols, nCP, osf);
symbolsDataReceived  = ofdmDemodulation(rData, nFFT, nDataOFDMsymbols, nCP, osf);

%% Frequency Equalization
symbolsDataEqualized = ofdmEqualization(symbolsDataReceived, symbolsPilotReceived, symbolsPilot);

%% Symbol demapping
bitsReceived = symbolDemapping(symbolsDataEqualized, M, modScheme);

%% Calculations
% Bit Error Rate (BER)
ber = mean(bitsData ~= bitsReceived);
fprintf("BER = %.3e\n", ber);

% Symbol Error Rate (SER)
errorSymbols = bit2int(bitsData, log2(M)) ~= bit2int(bitsReceived, log2(M));
ser = mean(errorSymbols);
fprintf("SER = %.2e\n", ser);

% Error Vector Magnitude (EVM)
evm = abs(symbolsDataEqualized - symbolsData);
fprintf('EVM = %.2f%%\n', rms(evm) * 100);

%% Constellation diagram
figure();
plot(symbolsDataEqualized(~errorSymbols), "."); hold("on");
plot(symbolsDataEqualized(errorSymbols), "r.");
plot(symbolMapping(int2bit(0 : M - 1, log2(M)), M, modScheme), "kx", LineWidth=2, MarkerSize=12);
axis("equal"); xlim([-1.5, 1.5]); ylim([-1.5, 1.5]);
xlabel("I"); ylabel("Q");
set(gca, XAxisLocation="origin", YAxisLocation="origin", XTick=[], YTick=[]);
title("Received constellation diagram");
legend("Correct symbols", "Incorrect symbols", Location="southoutside", Orientation="horizontal");


